hint.goodCode:
public boolean isEvenSquareBigger1000(int number) {
    if(!isEven(number)){
        return false
    }
    if(!isSquare(number)) {
        return false;
    }
    return number > 1000;
}

hint.badCode:
public boolean isEvenSquareBigger1000(int number) {
    int sqrt = (int) Math.sqrt(number);
    if (sqrt * sqrt != number) {
            return false;
    }
    if (sqrt % 2 != 0) {
        return false; 
    }
    return number > 1000;
}
