hint.goodCode:
public static void goodPrintOrderDetails(Order order) {
    Customer customer = order.getCustomer();
    Product product = order.getProduct();
    Address shippingAddress = order.getShippingAddress();
    String phoneNumber = customer.getPhoneNumber();  // Assuming you have these methods in your classes
    String orderStatus = order.getStatus();

    StringBuilder orderDetailsBuilder = new StringBuilder()
        .append("Order ID: ").append(order.getId())
        .append(", Customer Name: ").append(customer.getName())
        .append("\nProduct Category: ").append(product.getCategory())
        .append(", Price: $").append(product.getPrice())
        .append("\nQuantity: ").append(order.getQuantity())
        .append(", Shipping Address: ").append(shippingAddress.toAddressString())
        .append("\nPhone Number: ").append(phoneNumber)
        .append(", Status: ").append(orderStatus);

    System.out.println(orderDetailsBuilder.toString());
}

hint.badCode:
public static void goodPrintOrderDetails(Order order) {
    Customer customer = order.getCustomer();
    Product product = order.getProduct();
    Address shippingAddress = order.getShippingAddress();
    String phoneNumber = customer.getPhoneNumber();  // Assuming you have these methods in your classes
    String orderStatus = order.getStatus();

    StringBuilder orderDetailsBuilder = new StringBuilder()
    .append("Order ID: ").append(order.getId()).append(", Customer Name: ").append(customer.getName()).append("\nProduct Category: ").append(product.getCategory())
    .append(", Price: $").append(product.getPrice()).append("\nQuantity: ").append(order.getQuantity()).append(", Shipping Address: ").append(shippingAddress.toAddressString())
    .append("\nPhone Number: ").append(phoneNumber).append(", Status: ").append(orderStatus);

    System.out.println(orderDetailsBuilder.toString());
}
