hint.goodCode:
public static void performCalculations() {
    double firstFactor = (58 + 10 * 2.2);
    double secondFactor = (2 + 6 / 3.54 * 7);
    double thirdFactor = (25 * 2 / 2 * 8.4);

    int result = firstFactor * secondFactor * thirdFactor;
    System.out.println("Result of the calculations: " + result);
}

hint.badCode:
public static void performCalculations() {
    int result = (58 + 10 * 2.2) * (2 + 6 / 3.54 * 7) * (25 * 2 / 2 * 8.4);
    System.out.println("Result of the calculations: " + result);
}
