hint.goodCode:
/**
* Change the password of a user
* @param username a username to identify the user
* @param password the password to set
* @return weather the password was changed successfully or not
*/
public boolean changePassword(String username, String password) {
    User user = this.db.findUserByName(username);
    if(user == null) {
        return false; //null value indicates that no user was found
    }
    String hashed = this.hash(password);
    if(password.equals(hashed)) {
        return false; //do no allow current password
    }

    //blocked or archived users cannot change their password
    if(user.isBlocked() || user.isArchived()) {
        return false; 
    }
    user.setPassword(password);
    this.db.updateUser(user);
    return true;
}

hint.badCode:
public boolean changePassword(String username, String password) {
    User user = this.db.findUserByName(username);
    if(user == null) {
        return false; //null value indicates that no item was found
    }
    String hashed = this.hash(password);
    if(password.equals(hashed)) {
        return false; //do no allow current password
    }

    if(user.isBlocked() || user.isArchived()) {
        return false; 
    }
    user.setPassword(password);
    this.db.updateUser(user);
    return true;
}
