hint.goodCode:
public int gradeToNum(String input) {   
    if(input.equals("One")) { 
        return 1;
    }

    if(input.equals("Two")) {
        return 2;
    }

    if(input.equals("Three")) { 
        return 3; 
    }

    if(input.equals("Four")) {
        return 4; 
    }

    if(input.equals("Five")) {
        return 5;
    }

    return 6;
}

hint.badCode:
public int gradeToNum(String input) {   
    if(input.equals("One") || input.equals("Two")) { 
        return input.equals("One") ? 1 : 2
    }

    if(input.equals("Three")) { 
        return 3; 
    } else if(input.equals("Four")) {
        return 4; 
    }

    if(input.equals("Five")) {
        return 5;
    }

    return 6;
}
