hint.goodCode:
public void printMatrix(int[][] matrix, int rows, int cols) {
    for (int i = 0; i < rows ; i++) {
        for (int j = 0; j < cols; j++) {
            System.out.println("Row: %d Column: %d Value: %d".formatted(i,j,matrix[i][j]));
        }
    }
}

hint.badCode:
public void printMatrix(int[][] matrix, int rows, int cols) {
    for (int i = 0; i < rows ; i++) {
        int j = 0;
        while (j < cols) {
            System.out.println("Row: %d Column: %d Value: %d".formatted(i,j,matrix[i][j]));
            j++;
        }
    }
}
