hint.goodCode:
public static void printInDifferentCurrencies(double dollars) {
    printUsdToEur(dollars);
    printUsdToGbp(dollars);
    printUsdToJpy(dollars);
    printUsdToAud(dollars);
    printUsdToCad(dollars);
    printUsdToChf(dollars);
    printUsdToInr(dollars);
}

hint.badCode:
public static void printInDifferentCurrencies(double dollars) {
    double euroRate = 0.85;
    double poundRate = 0.75;
    double yenRate = 110.0;
    double audRate = 1.35;
    double cadRate = 1.25;
    double chfRate = 0.92;
    double inrRate = 73.0;

    double euros = dollars * euroRate;
    System.out.println(dollars + " USD is " + euros + " EUR");

    double pounds = dollars * poundRate;
    System.out.println(dollars + " USD is " + pounds + " GBP");

    double yen = dollars * yenRate;
    System.out.println(dollars + " USD is " + yen + " JPY");

    double aud = dollars * audRate;
    System.out.println(dollars + " USD is " + aud + " AUD");

    double cad = dollars * cadRate;
    System.out.println(dollars + " USD is " + cad + " CAD");

    double chf = dollars * chfRate;
    System.out.println(dollars + " USD is " + chf + " CHF");

    double inr = dollars * inrRate;
    System.out.println(dollars + " USD is " + inr + " INR");
}
